CREATE TABLE IF NOT EXISTS "insurance_order" (
    "id" bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY,
    "order_id" varchar NOT NULL,
    "insr_type" varchar NOT NULL,
    "project_name" varchar NOT NULL,
    "provincial" varchar NOT NULL,
    "city" varchar NOT NULL,
    "district" varchar NOT NULL,
    "street" varchar NOT NULL,
    "insured_username" varchar NOT NULL,
    "insr_start_time" varchar NOT NULL,
    "insr_end_time" varchar NOT NULL,
    "total" int NOT NULL,
    "total_straight" int NOT NULL,
    "total_escalator" int NOT NULL,
    "organization_name" varchar NOT NULL,
    "comments" varchar,
    "has_part" varchar NOT NULL,
    "creator" varchar DEFAULT '',
    "create_time" datetime NOT NULL DEFAULT CURRENT_TIMESTAMP,
    "updater" varchar DEFAULT '',
    "update_time" datetime NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
    "deleted" bit NOT NULL DEFAULT FALSE,
    "tenant_id" bigint NOT NULL,
    PRIMARY KEY ("id")
) COMMENT '保单';

